<template>    <form name="myform" novalidate="novalidate">        <div class="form-group">            <label for="bookName" class="control-label">请输入书名</label>            <input type="text" class="form-control" id="bookName" placeholder="请输入图书名称" v-model="bookName"                   autocomplete="off">        </div>        <div class="form-group">            <label class="control-label" for="bookCover">请输入封面地址</label>            <input type="text" class="form-control" placeholder="请输入封面地址" v-model="bookCover" id="bookCover"                   autocomplete="off">        </div>        <div class="form-group">            <label for="bookPrice" class="control-label">请输入价格</label>            <input type="text" class="form-control" placeholder="请输入价格" id="bookPrice" v-model="bookPrice"                   autocomplete="off">        </div>        <div class="form-group">            <input type="button" @click="addBook()" value="添加图书" class="btn btn-danger">        </div>    </form></template><script>    export default {        data(){            return {              book:{                  bookName:'',                  bookPrice:'',                  bookCover:''              }            }        },        components:{},        methods:{            addBook(){                this.$http.post('/book',this.book).then(()=>{                  this.$router.push('/list')//成功后跳转到list                })            }        }    }</script><style></style>