<template>    <div>        <div>            <div class="panel panel-warning">                <div class="panel-heading">                    书名:{{book.bookName}}                    <input type="text" v-model="book.bookName"/>                </div>                <div class="panel-body text-center">                    <img :src="book.bookCover" alt=""/>                </div>                <div class="panel-footer">                    价格:{{book.bookPrice | currency('￥') }}                    <input type="text" v-model="book.bookPrice"/>                    <button class="btn btn-danger" @click="remove">删除</button>                    <button class="btn btn-default" @click="update">修改</button>                </div>            </div>        </div>    </div></template><script>    export default {        filters:{            currency(input,param1){//参1  值 参2 内容                return param1 + input            }        },        created(){            //页面已加载获取数据获取id对应的数据           let id= this.$route.params.id;           this.$http.get('/book?id='+id).then((res)=>{               this.book=res.body;//从页面拿到数据给book           })        },        data(){            return {                book:{                    bookName:'',                    bookCover:'',                    bookPrice:''                }            }        },        components:{},        methods: {            update() {                this.$http.put('/book', this.book).then(res=>{                    this.$router.push('/list');                }).catch(err => {                    console.log(err);                })            },            remove() {                this.$http.delete('/book?id='+this.book.id).then(res =>{                    this.$router.push('/list');                }).catch(err => {                    console.log(err);                });            }        }    }</script><style scoped>    img{        width: 100%;    }</style>